function GameMainViewController.OnOpen()
    if modelView == nil then
        --没有初始化 UI
        DispatchEvent(GameMainConfig.eventNotification.OPEN_GAMEMAIN_PANEL)
    else
        ---直接调用
        modelView:GetGameMainUIView():OnShow()
    end
end


